    user_index = User.where("id != #{user_id}").pluck(:id)
    result, result1 = pcs_array.zip(user_index).sort_by(&:first).reverse.transpose
    top_5 = result1.first(5)
    top_5_genres = []
    @similar_users = top_5
  end
end
